// Transform Adzuna response to clean job data
const results = $input.item.json.results || [];

const jobs = results.map(job => {
  return {
    job_id: job.id || '',
    title: job.title || '',
    company: job.company?.display_name || 'Unknown',
    location: job.location?.display_name || 'Not specified',
    salary_min: job.salary_min || -1,
    salary_max: job.salary_max || -1,
    salary_display: job.salary_is_predicted ? 'Predicted' : 'Actual',
    description: job.description || '',
    url: job.redirect_url || '',
    posted_date: job.created || '',
    scraped_date: new Date().toISOString(),
    category: job.category?.label || '',
    contract_type: job.contract_type || '',
    status: 'new',
    full_description: ''
  };
});

return jobs

// Log success with details
const itemCount = $input.all().length;
return [{
  json: {
    success: true,
    new_jobs_added: itemCount,
    timestamp: new Date().toISOString(),
    message: `Successfully added ${itemCount} new jobs (duplicates filtered)`
  }
}];

// Log when no new jobs found
return [{
  json: {
    success: true,
    new_jobs_added: 0,
    timestamp: new Date().toISOString(),
    message: 'No new jobs found - all were duplicates'
  }
}];
